import { Link } from 'react-router-dom';

export function Home(props) {
  // Prevent scrolling
  document.body.style.overflow = 'hidden';

  return (
    <div className="container">
      <h2 className="title">Bus Booking Website</h2>
      <p className="description">Easy website for booking buses and searching buses!!</p>
      <p className="description">Also, add and delete bus details dynamically in a few clicks.</p>
      <div className="d-flex justify-content-center align-items-center vh-100">
        <div className="buttons d-flex flex-column align-items-center">
          <button className="btn btn-primary adminButton">Login as Admin</button>
          <Link to="/user" className="btn btn-primary userButton mt-3">Login as User</Link>
        </div>
      </div>
    </div>
  );
}
