import { useState } from 'react';
import { Link } from 'react-router-dom';

export function UserComponent() {
  const [isLoggedIn, setIsLoggedIn] = useState(false);

  const handleLogin = () => {
    // Perform login logic here
    setIsLoggedIn(true);
  };

  const handleLogout = () => {
    // Perform logout logic here
    setIsLoggedIn(false);
  };

  return (
    <div className="user-login">
    {!isLoggedIn ? (
      <div className="login-form">
        <h2>Login as User</h2>
        <button className="btn btn-primary" onClick={handleLogin}>Login</button>
      </div>
    ) : (
      <div className="user-dashboard">
        <h2>Welcome, User!</h2>
        <button className="btn btn-danger" onClick={handleLogout}>Logout</button>
        <div className="search-buttons">
          <Link to="/searchBus" className="btn btn-primary">Search Bus</Link>
          <Link to="/searchBusWithName" className="btn btn-primary">Search Bus with Name</Link>
          <Link to="/searchWithNumberPlate" className="btn btn-primary">Search with Number Plate</Link>
        </div>
      </div>
    )}
  </div>
  );
}
