import React, { Component } from 'react';
import BusService from '../services/BusService';

class BusListComponent extends Component {
  constructor(props) {
    super(props);
    this.state = {
      buses: []
    };
  }

  componentDidMount() {
    BusService.getBuses().then((res) => {
      this.setState({ buses: res.data });
    });
  }

  deleteBus(id) {
    // Call the delete endpoint of the Spring Boot controller using the provided ID
    BusService.deleteBus(id).then((res) => {
      console.log(res.data); // Log the response from the server
      // Remove the deleted bus from the state
      this.setState({
        buses: this.state.buses.filter((bus) => bus.id !== id)
      });
    });
  }

  render() {
    return (
      <div>
        <h2 className="text-center">Bus List</h2>
        <div className="row">
          <table className="table table-striped table-bordered">
            <thead>
              <tr>
                <th>Number Plate</th>
                <th>Bus Name</th>
                <th>Source</th>
                <th>Destination</th>
                <th>Capacity</th>
                <th>Seat Price</th>
                <th>Starting Time</th>
                <th>Arrival Time</th>
                <th>Duration</th>
                <th>Actions</th>
              </tr>
            </thead>

            <tbody>
              {this.state.buses.map((bus) => (
                <tr key={bus.id}>
                  <td>{bus.numberPlate}</td>
                  <td>{bus.busName}</td>
                  <td>{bus.source}</td>
                  <td>{bus.destination}</td>
                  <td>{bus.capacity}</td>
                  <td>{bus.seatPrice}</td>
                  <td>{bus.startingTime}</td>
                  <td>{bus.arrivalTime}</td>
                  <td>{bus.duration}</td>
                  <td>
                    <button
                      onClick={() => this.deleteBus(bus.id)}
                      className="btn btn-danger btn-sm"
                    >
                      Delete
                    </button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </div>
    );
  }
}

export default BusListComponent;
