import { useState } from 'react';
import { Link } from 'react-router-dom';

export function AdminComponent() {
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');

  const handleLogin = () => {
    // Perform login logic here with username and password
    if (username === 'admin' && password === 'password') {
      setIsLoggedIn(true);
    }
  };

  const handleLogout = () => {
    // Perform logout logic here
    setIsLoggedIn(false);
    window.location = '/';
  };

  // Render the admin-welcome section when isLoggedIn is true
  if (isLoggedIn===true) {
    return (
      <div className="container d-flex flex-column align-items-center mt-5">
        <div className="admin-welcome">
          <div className="welcome-message mb-3">
            <h2>Welcome, Admin!</h2>
          </div>
          <div className="search-buttons">
            <div className="mb-3">
              <Link to="/addbus" className="btn btn-primary">
                Register Bus
              </Link>
            </div>
            <div className="mb-3">
              <Link to="/getAllBuses" className="btn btn-primary">
                View All Buses
              </Link>
            </div>
            <div className="mb-3">
              <Link to="/numberplate" className="btn btn-primary">
                Search by Number Plate
              </Link>
            </div>
          </div>
          <div className="logout-button mt-5">
            <button className="btn btn-danger" onClick={handleLogout}>
              Logout
            </button>
          </div>
        </div>
      </div>
    );
  }

  // Render the login form when isLoggedIn is false
  return (
    <div className="container d-flex flex-column align-items-center mt-5">
      <div className="login-form d-flex flex-column align-items-center">
        <h2 className="mb-3">Admin Login</h2>
        <div className="mb-3">
          <input
            type="text"
            placeholder="Username"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
            required
          />
        </div>
        <div className="mb-3">
          <input
            type="password"
            placeholder="Password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
          />
        </div>
        <button className="btn btn-primary" onClick={handleLogin}>
          Login
        </button>
      </div>
    </div>
  );
}
