import React, { useState } from 'react';
import axios from 'axios';

const SearchBus = () => {
    const [source, setSource] = useState('');
    const [destination, setDestination] = useState('');
    const [searchResults, setSearchResults] = useState([]);
    const [searchClick, setSearchClicked] = useState(false);
    const [error, setError] = useState('');
    const [errorMessage, setErrorMessage] = useState('');

const handleSearch = () => {
    setError('');
    setSearchResults([]);
    setErrorMessage('Bus not found, ');
    
    axios.get(`http://localhost:8090/searchbus/${source}/${destination}`)
        // .then(response=>{
        //     if(response.status===404){
        //       setError('Sorry bus not found from ..');
        //     }
        //     return response.data;
        // })
        // .then(data=> {
        //     if(data.length===0){
        //         setError('Sorry bus not found from ..');
        //     }
        //     else{
        //         setSearchResults(data);
        //     }
        // })
        // .catch(error => {
        //     setError(error.message);
        //     console.error(error);
        // });
    setSearchClicked(true);
    };

    return (
    <div>
      <h1>Bus Search</h1>
      <input
      type="text"
      placeholder="Source"
      value={source}
      onChange={(e) => setSource(e.target.value)}
      />
      <input
      type="text"
      placeholder="Destination"
      value={destination}
      onChange={(e) => setDestination(e.target.value)}
      />
      <button onClick={handleSearch}>Search</button>
      {searchClick && (
        <div>
          <h2>Search Results</h2>
          {searchResults.length===0? (
            errorMessage && (
              <p className="error"> {errorMessage} from {source} to {destination} </p>
            )
      ): (
      <table className='table table-striped table-bordered'>
        <thead>
          <tr>
            <th>Number Plate</th>
            <th>Bus Name</th>
            <th>Capacity</th>
            <th>Seat Price</th>
            <th>Starting Time</th>
            <th>Arrival Time</th>
            <th>Duration</th>
            <th>Booking</th>
            </tr>
            </thead>
            <tbody>
              {searchResults.map((bus) => (
              <tr key={bus.numberPlate}>
                <td>{bus.numberPlate}</td>
                <td>{bus.busName}</td>
                <td>{bus.capacity}</td>
                <td>{bus.seatPrice}</td>
                <td>{bus.startingTime}</td>
                <td>{bus.arrivalTime}</td>
                <td>{bus.duration}</td>
                <td><input className='btn btn-primary' type="submit" value="Book"></input></td>
                </tr>
                ))}
              </tbody>
            </table>
          )}
        </div>
      )}
      </div>
    );



};

export default SearchBus;
