import React, { useState } from 'react';
import axios from 'axios';

const SearchWithNumberPlate = () => {
  const [numberPlate, setNumberPlate] = useState('');
  const [searchResults, setSearchResults] = useState([]);
  const [searchComplete, setSearchComplete] = useState(false);
  const [error, setError] = useState('');
  const [searchClick, setSearchClicked] = useState(false);

  const handleSearch = () => {
    setError('');
    setSearchResults([]);

    axios
      .get(`http://localhost:8090/getBusByNumberPlate/${numberPlate}`)
      .then(response => {
        if (response.status === 402) {
          throw new Error(`No buses found with bus name equal to ${busName} from ${source} to ${destination}`);
        }
        return response.data;
      })
      .then(data => {
        if (data.length === 0) {
          setError('Error, Bus not found.');
        } else {
          setSearchResults(data);
        }
      })
      .catch(error => {
        console.log(error.response.data.message);
        setError(error.response.data.message);
      });
    setSearchClicked(true);
  };

  return (
    <div>
      <h1>Bus Search</h1>
      <input
        type="text"
        placeholder="Number Plate"
        value={numberPlate}
        onChange={(e) => setNumberPlate(e.target.value)}
      />
      <button className="btn btn-primary" onClick={handleSearch}>
        Search
      </button>
      {searchComplete && (
        <div>
          <h2>Search Results</h2>
          {error ? (
            <p>Sorry, No Bus found with {numberPlate} !!</p>
          ) : (
            <table className="table table-striped table-bordered">
              <thead>
                <tr>
                  <th>Number Plate</th>
                  <th>Bus Name</th>
                  <th>Capacity</th>
                  <th>Seat Price</th>
                  <th>Starting Time</th>
                  <th>Arrival Time</th>
                  <th>Duration</th>
                </tr>
              </thead>
              <tbody>
                {searchResults.map((bus) => (
                  <tr key={bus.numberPlate}>
                    <td>{bus.numberPlate}</td>
                    <td>{bus.busName}</td>
                    <td>{bus.capacity}</td>
                    <td>{bus.seatPrice}</td>
                    <td>{bus.startingTime}</td>
                    <td>{bus.arrivalTime}</td>
                    <td>{bus.duration}</td>
                  </tr>
                ))}
              </tbody>
            </table>
          )}
        </div>
      )}
    </div>
  );
};

export default SearchWithNumberPlate;
