import { useState } from 'react';
import { Link } from 'react-router-dom';

export function UserComponent() {
  const [isLoggedIn, setIsLoggedIn] = useState(false);

  const handleLogin = () => {
    // Perform login logic here
    setIsLoggedIn(true);
  };

  const handleLogout = () => {
    // Perform logout logic here
    setIsLoggedIn(false);
  };

  return (
    <>
      <nav className="navbar navbar-expand-lg navbar-dark bg-dark">
        <Link to="/" className="navbar-brand">Pro Bus</Link>
        <div className="collapse navbar-collapse">
          <ul className="navbar-nav ml-auto">
            {!isLoggedIn ? (
              <li className="nav-item">
                <button className="btn btn-primary" onClick={handleLogin}>Login</button>
              </li>
            ) : (
              <li className="nav-item">
                <button className="btn btn-danger" onClick={handleLogout}>Logout</button>
              </li>
            )}
          </ul>
        </div>
      </nav>

      <div className="container mt-4">
        {!isLoggedIn ? (
          <div className="login-form">
            <h2>Login as User</h2>
            <button className="btn btn-primary" onClick={handleLogin}>Login</button>
          </div>
        ) : (
          <div>
            <h2>Welcome, User!</h2>
            <div className="search-buttons mt-3">
              <Link to="/searchBus" className="btn btn-primary mb-3">Search Bus</Link>
              <br></br>
              <Link to="/searchBusWithName" className="btn btn-primary mb-3">Search Bus with Name</Link>
              <br></br>
              <Link to="/searchWithNumberPlate" className="btn btn-primary mb-3">Search with Number Plate</Link>
            </div>
          </div>
        )}
      </div>
    </>
  );
}
