import React, { useState } from 'react';

const TransactionForm = ({ bus, handleTransaction }) => {
  const [noOfPassenger, setNoOfPassenger] = useState(1);
  const [tripDate, setTripDate] = useState('');
  const [email, setEmail] = useState('');
  const [passengerList, setPassengerList] = useState([]);
//   const [totalPrice, setTotalPrice] = useState(0);
  const [transactionDetails, setTransactionDetails] = useState(null); 

  const handleSubmit = () => {
    const transactionModel = {
      numberPlate: bus.numberPlate,
      source: bus.source,
      destination: bus.destination,
      startingTime: bus.startingTime,
      noOfPassenger,
      transactionId: 0, // Assign a valid transaction ID if required
      tripDate,
      email,
      passengerList,
    };

    handleTransaction(transactionModel)
        .then((response) => {
            // Handle the response from the transaction endpoint
            const transactionDetails = response.data;
        setTransactionDetails(transactionDetails);
      })
      .catch((error) => {
        // Handle errors if the transaction fails
        console.error('Transaction failed:', error);
      });
  };

  //handle passenger list if required
  const handlePassengerChange = (index, field, value) => {
    const updatedPassengerList = [...passengerList];
    if (!updatedPassengerList[index]) {
      updatedPassengerList[index] = {};
    }
    updatedPassengerList[index][field] = value;
    setPassengerList(updatedPassengerList);
  };
  return (
    <div>
      <h2>Transaction Form</h2>
      {/* Render form inputs for the transaction details */}
      <label>Number Plate:</label>
      <input type="text" value={bus.numberPlate} disabled />
      <label>Source:</label>
      <input type="text" value={bus.source} disabled />
      <label>Destination:</label>
      <input type="text" value={bus.destination} disabled />
      <label>Starting Time:</label>
      <input type="text" value={bus.startingTime} disabled />
      <label>No. of Passengers:</label>
      <input
        type="number"
        value={noOfPassenger}
        onChange={(e) => setNoOfPassenger(parseInt(e.target.value))}
      />
      {/* Render additional form inputs if required */}
      <label>Trip Date:</label>
      <input
        type="text"
        value={tripDate}
        onChange={(e) => setTripDate(e.target.value)}
      />
      <label>Email:</label>
      <input
        type="text"
        value={email}
        onChange={(e) => setEmail(e.target.value)}
      />
      {/* Render passenger forms dynamically based on the number of passengers */}
      {Array.from({ length: noOfPassenger }).map((_, index) => (
  <div key={index}>
    <h3>Passenger {index + 1}</h3>
    <label>Name:</label>
    <input
      type="text"
      value={passengerList[index]?.passengerName || ''}
      onChange={(e) => handlePassengerChange(index, 'passengerName', e.target.value)}
    />
    <label>Age:</label>
    <input
      type="number"
      value={passengerList[index]?.passengerAge || ''}
      onChange={(e) => handlePassengerChange(index, 'passengerAge', parseInt(e.target.value))}
    />
    <label>Gender:</label>
    <select
      value={passengerList[index]?.passengerGender || ''}
      onChange={(e) => handlePassengerChange(index, 'passengerGender', e.target.value)}
    >
      <option value="">Select Gender</option>
      <option value="Male">Male</option>
      <option value="Female">Female</option>
      <option value="Other">Other</option>
    </select>
  </div>
))}
    
    <button onClick={handleSubmit}>Submit</button>
    <p>{response.data.totalPrice}</p>
    {transactionDetails && (
        <div>
          <p>Transaction successful:</p>
          <p>Transaction ID: {transactionDetails.transactionId}</p>
          <p>No. of Passengers: {transactionDetails.noOfPassenger}</p>
          <p>Seat Price: {transactionDetails.seatPrice}</p>
          <p>Total Price: {transactionDetails.totalPrice}</p>
          <button>Pay</button>
          {/* Display other transaction details as needed */}
        </div>
      )}
    {/* <p>You have to Pay: {totalPrice}</p> */}
    </div>
  );
};

export default TransactionForm;
