import React, {useState, useEffect} from 'react'
import {Link, useHistory, useParams } from 'react-router-dom';
import BusService from '../services/BusService';

const AddBussComponent = () => {

    const [numberPlate, setNumberPlate] = useState('')
    const [busName, setBusName] = useState('')
    const [seatPrice, setSeatPrice] = useState('')
    const history = useHistory();
    const {id} = useParams();

    const saveOrUpdateBus = (e) => {
        e.preventDefault();

        const bus = {numberPlate, busName, seatPrice}

        if(id){
            BusService.updateEmployee(id, employee).then((response) => {
                history.push('/employees')
            }).catch(error => {
                console.log(error)
            })
        }else{
        BusService.addBus(bus).then((response) =>{

                console.log(response.data)
    
                history.push('/getAllBuses');
    
            }).catch(error => {
                console.log(error)
            })
        }
        
    }

    useEffect(() => {

        BusService.getBusById(id).then((response) =>{
            setNumberPlate(response.data.numberPlate)
            setBusName(response.data.busName)
            setSeatPrice(response.data.seatPrice)
        }).catch(error => {
            console.log(error)
        })
    }, [])

    const title = () => {

        if(id){
            return <h2 className = "text-center">Update Employee</h2>
        }else{
            return <h2 className = "text-center">Add Employee</h2>
        }
    }

    return (
        <div>
           <br /><br />
           <div className = "container">
                <div className = "row">
                    <div className = "card col-md-6 offset-md-3 offset-md-3">
                       {
                           title()
                       }
                        <div className = "card-body">
                            <form>
                                <div className = "form-group mb-2">
                                    <label className = "form-label"> Number Plate:</label>
                                    <input
                                        type = "text"
                                        placeholder = "Enter Number Plate"
                                        name = "numberPlate"
                                        className = "form-control"
                                        value = {numberPlate}
                                        onChange = {(e) => setNumberPlate(e.target.value)}
                                    >
                                    </input>
                                </div>

                                <div className = "form-group mb-2">
                                    <label className = "form-label"> Bus Name:</label>
                                    <input
                                        type = "text"
                                        placeholder = "Enter Bus Name"
                                        name = "busName"
                                        className = "form-control"
                                        value = {busName}
                                        onChange = {(e) => setBusName(e.target.value)}
                                    >
                                    </input>
                                </div>

                                <div className = "form-group mb-2">
                                    <label className = "form-label"> Seat Price:</label>
                                    <input
                                        type = "email"
                                        placeholder = "Enter Seat Price"
                                        name = "seatPrice"
                                        className = "form-control"
                                        value = {seatPrice}
                                        onChange = {(e) => setSeatPrice(e.target.value)}
                                    >
                                    </input>
                                </div>

                                <button className = "btn btn-success" onClick = {(e) => saveOrUpdateBus(e)} >Submit </button>
                                <Link to="/getAllBuses" className="btn btn-danger"> Cancel </Link>
                            </form>

                        </div>
                    </div>
                </div>

           </div>

        </div>
    )
}

export default AddBussComponent
