import React, { useState } from 'react';
import axios from 'axios';

const SearchWithNumberPlate = () => {
    const [numberPlate, setnumberPlate] = useState('');
    const [searchResults, setSearchResults] = useState([]);
    const [searchClick, setSearchClicked] = useState(false);
    const [error, setError] = useState('');

const handleSearch = () => {
    setError('');
    setSearchResults([]);
    
    axios.get(`http://localhost:8090/getBusByNumberPlate/numberPlate/${numberPlate}`)
        .then(response=>{
            if(response.status===402){
                throw new Error("v");
            }
            return response.data;
        })
        .then(data=> {
            if(data.length===0){
                setError(' ');
            }
            else{
                setSearchResults(data);
            }
        })
        .catch(error => {
            setError(error.message);
            console.error(error);
        });
    setSearchClicked(true);
    };

    return (
    <div>
      <h1>Bus Search</h1>
      <input
      type="text"
      placeholder="Number Plate"
      value={numberPlate}
      onChange={(e) =>setnumberPlate(e.target.value)}
      />
      <button onClick={handleSearch}>Search</button>
      {searchClick && (
        <div>
          <h2>Search Results</h2>
          {searchResults.length===0? (
        <p>Sorry, No Bus found with {numberPlate} !!</p>
      ): (
      <table className='table table-striped table-bordered'>
        <thead>
          <tr>
            <th>Number Plate</th>
            <th>Bus Name</th>
            <th>Capacity</th>
            <th>Seat Price</th>
            <th>Starting Time</th>
            <th>Arrival Time</th>
            <th>Duration</th>
            </tr>
            </thead>
            <tbody>
              {searchResults.map((bus) => (
              <tr key={bus.numberPlate}>
                <td>{bus.numberPlate}</td>
                <td>{bus.busName}</td>
                <td>{bus.capacity}</td>
                <td>{bus.seatPrice}</td>
                <td>{bus.startingTime}</td>
                <td>{bus.arrivalTime}</td>
                <td>{bus.duration}</td>
                </tr>
                ))}
              </tbody>
            </table>
          )}
        </div>
      )}
      </div>
    );



};

export default SearchWithNumberPlate;
