import React from 'react';

const Payment = ({ transaction, data }) => {
  // Handle payment process and display payment details
  const handlePayment = () => {
    // Implement your payment logic here
    console.log('Payment process initiated');
  };

  return (
    <div>
      <h2>Payment Details</h2>
      <p>Transaction ID: {transaction.transactionId}</p>
      <p>No. of Passengers: {transaction.noOfPassenger}</p>
      <p>Seat Price: {transaction.seatPrice}</p>
      <p>Total Price: {transaction.totalPrice}</p>
      <p>Discount: {transaction.discount}</p>
      <button className="btn btn-primary" onClick={handlePayment}>
        Pay
      </button>
      <div>
        {/* Display additional data received from the backend */}
        {data && (
          <>
            <h3>Additional Data</h3>
            {/* Render additional data as per your requirement */}
            <pre>{JSON.stringify(data, null, 2)}</pre>
          </>
        )}
      </div>
    </div>
  );
};

export default Payment;
