import React, { useState, useEffect } from 'react';
import axios from 'axios';
import Payment from './Payment';
import ReactDOM from 'react-dom/client';
import DatePicker from 'react-datepicker';
import 'react-datepicker/dist/react-datepicker.css';

const SearchBus = () => {
  const [source, setSource] = useState('');
  const [destination, setDestination] = useState('');
  const [searchResults, setSearchResults] = useState([]);
  const [searchClick, setSearchClicked] = useState(false);
  const [error, setError] = useState('');
  const [errorMessage, setErrorMessage] = useState('');
  const [redirect, setRedirect] = useState(false);
  const [selectedBus, setSelectedBus] = useState(null);
  const [showResultsTable, setShowResultsTable] = useState(true);
  const [noOfPassenger, setNoOfPassenger] = useState(1);
  const [tripDate, setTripDate] = useState('');
  const [email, setEmail] = useState('');
  const [passengerList, setPassengerList] = useState([]);
  const [totalPrice, setTotalPrice] = useState([]);
  const [transactionDetails, setTransactionDetails] = useState(null);
  const [data, setData] = useState([]);

  const handleSearch = () => {
    setError('');
    setSearchResults([]);
    setErrorMessage('Bus not found');

    axios
      .get(`http://localhost:8090/searchbus/${source}/${destination}`)
      .then((response) => {
        if (response.status === 404) {
          setError('');
        }
        return response.data;
      })
      .then((data) => {
        if (data.length === 0) {
          setError('');
        } else {
          setSearchResults(data);
        }
      })
      .catch((error) => {
        setError(error.message);
        console.error(error);
      });
    setSearchClicked(true);
  };

  const handleBooking = (bus) => {
    setSelectedBus(bus);
    setRedirect(true);
    setShowResultsTable(false); // Hide the search results table
  };

  const handleTransaction = (transactionModel) => {
    return axios
      .post('http://localhost:8090/maketransaction', transactionModel)
      .then((response) => {
        // Handle the response from the transaction endpoint
        console.log('Transaction successful:', response.data);
        return response.data;
      })
      .catch((error) => {
        // Handle errors if the transaction fails
        console.error('Transaction failed:', error);
        throw error;
      });
  };

  const handleSubmit = () => {
    const transactionModel = {
      numberPlate: selectedBus.numberPlate,
      source: selectedBus.source,
      destination: selectedBus.destination,
      startingTime: selectedBus.startingTime,
      noOfPassenger,
      transactionId: 0, // Assign a valid transaction ID if required
      tripDate,
      email,
      passengerList,
      // totalPrice,
    };

    handleTransaction(transactionModel)
      .then((response) => {
        // Handle the response from the transaction endpoint
        const transactionDetails = response.data;
        setTransactionDetails(transactionDetails);
        const root = ReactDOM.createRoot(document.getElementById('root'));
        root.render(<Payment transaction={transactionDetails} />);
      })
      .catch((error) => {
        // Handle errors if the transaction fails
        console.error('Transaction failed:', error);
      });
  };

  const renderTransactionDetails = () => {
    if (transactionDetails) {
      return (
        <div>
          <p>Transaction successful:</p>
          <p>Transaction ID: {transactionDetails.transactionId}</p>
          <p>No. of Passengers: {transactionDetails.noOfPassenger}</p>
          <p>Seat Price: {transactionDetails.seatPrice}</p>
          <p>Total Price: {transactionDetails.totalPrice}</p>
          <button className="btn btn-primary">Pay</button>
        </div>
      );
    } else {
      return null;
    }
  };

  useEffect(() => {
    console.log('Transaction details:', transactionDetails);
    if (transactionDetails) {
      setData((prevData) => [...prevData, transactionDetails]);
    }
  }, [transactionDetails]);

  const handlePassengerChange = (index, field, value) => {
    const updatedPassengerList = [...passengerList];
    if (!updatedPassengerList[index]) {
      updatedPassengerList[index] = {};
    }
    updatedPassengerList[index][field] = value;
    setPassengerList(updatedPassengerList);
  };

  const today = new Date(); // Get the current date
  today.setHours(0, 0, 0, 0); // Set the time to midnight

  return (
    <div className="container" >
      <h1>Search Bus</h1>
      <div className="form-group mb-3">
        <input
          type="text"
          className="form-control"
          placeholder="Source"
          value={source}
          onChange={(e) => setSource(e.target.value)}
        />
      </div>
      <div className="form-group mb-3">
        <input
          type="text"
          className="form-control"
          placeholder="Destination"
          value={destination}
          onChange={(e) => setDestination(e.target.value)}
        />
      </div>
      <button className="btn btn-primary mb-3" onClick={handleSearch}>
        Search
      </button>
      {searchClick && (
        <div>
          {searchResults.length === 0 ? (
            errorMessage && (
              <p className="error">
                {errorMessage} from {source} to {destination}
              </p>
            )
          ) : (
            showResultsTable && (
              <>
                <h2>Search results</h2>
                <table className="table table-striped table-bordered">
                  <thead>
                    <tr>
                      <th>Number Plate</th>
                      <th>Bus Name</th>
                      <th>Capacity</th>
                      <th>Seat Price</th>
                      <th>Starting Time</th>
                      <th>Arrival Time</th>
                      <th>Duration</th>
                      <th>Booking</th>
                    </tr>
                  </thead>
                  <tbody>
                    {searchResults.map((bus) => (
                      <tr key={bus.numberPlate}>
                        <td>{bus.numberPlate}</td>
                        <td>{bus.busName}</td>
                        <td>{bus.capacity}</td>
                        <td>{bus.seatPrice}</td>
                        <td>{bus.startingTime}</td>
                        <td>{bus.arrivalTime}</td>
                        <td>{bus.duration}</td>
                        <td>
                          <button
                            className="btn btn-primary"
                            onClick={() => handleBooking(bus)}
                          >
                            Book
                          </button>
                        </td>
                      </tr>
                    ))}
                  </tbody>
                </table>
              </>
            )
          )}
        </div>
      )}
      {redirect && selectedBus && (
        <div style={{ overflowY: 'auto', maxHeight: '500px' }}>
  <h2>Transaction Form</h2>
  <div className="form-group" style={{ overflowY: 'auto', maxHeight: '500px' }}>
    <label>Number Plate:</label>
    <input
      type="text"
      className="form-control"
      value={selectedBus.numberPlate}
      disabled
    />
  </div>
  <div className="form-group">
    <label>Source:</label>
    <input
      type="text"
      className="form-control"
      value={selectedBus.source}
      disabled
    />
  </div>
  <div className="form-group">
    <label>Destination:</label>
    <input
      type="text"
      className="form-control"
      value={selectedBus.destination}
      disabled
    />
  </div>
  <div className="form-group">
    <label>Starting Time:</label>
    <input
      type="text"
      className="form-control"
      value={selectedBus.startingTime}
      disabled
    />
  </div>
  <div className="form-group">
    <label>No. of Passengers:</label>
    <input
      type="number"
      className="form-control"
      value={noOfPassenger}
      onChange={(e) => setNoOfPassenger(parseInt(e.target.value))}
    />
  </div>
  <div className="form-group">
    <label>Trip Date:</label>
    <DatePicker
      selected={tripDate}
      onChange={(date) => setTripDate(date)}
      minDate={today}
      className="form-control"
    />
  </div>
  <div className="form-group">
    <label>Email:</label>
    <input
      type="text"
      className="form-control"
      value={email}
      onChange={(e) => setEmail(e.target.value)}
    />
  </div>
  <div className='form-group' style={{ overflowY: 'auto', maxHeight: '300px' }}>
    {Array.from({ length: noOfPassenger }).map((_, index) => (
      <div key={index}>
        <h3>Passenger {index + 1}</h3>
        <div className="form-group">
          <label>Name:</label>
          <input
            type="text"
            className="form-control"
            value={passengerList[index]?.passengerName || ''}
            onChange={(e) =>
              handlePassengerChange(index, 'passengerName', e.target.value)
            }
          />
        </div>
        <div className="form-group">
          <label>Age:</label>
          <input
            type="number"
            className="form-control"
            value={passengerList[index]?.passengerAge || ''}
            onChange={(e) =>
              handlePassengerChange(index, 'passengerAge', parseInt(e.target.value))
            }
          />
        </div>
        <div className="form-group">
          <label>Gender:</label>
          <select
            className="form-control"
            value={passengerList[index]?.passengerGender || ''}
            onChange={(e) =>
              handlePassengerChange(index, 'passengerGender', e.target.value)
            }
          >
            <option value="">Select Gender</option>
            <option value="Male">Male</option>
            <option value="Female">Female</option>
            <option value="Other">Other</option>
          </select>
        </div>
      </div>
    ))}
  </div>
  <button className="btn btn-primary" onClick={handleSubmit}>
    Submit
  </button>
  {renderTransactionDetails()}
</div>

      )}
    </div>
  );
};

