import React, { useState } from 'react';
import axios from 'axios';
import TransactionForm from './TransactionForm';

const SearchBus = () => {
    const [source, setSource] = useState('');
    const [destination, setDestination] = useState('');
    const [searchResults, setSearchResults] = useState([]);
    const [searchClick, setSearchClicked] = useState(false);
    const [error, setError] = useState('');
    const [errorMessage, setErrorMessage] = useState('');
    const [redirect, setRedirect] = useState(false);
    const [selectedBus, setSelectedBus] = useState(null);
    const [showResultsTable, setShowResultsTable] = useState(true);
    const handleSearch = () => {
      setError('');
      setSearchResults([]);
      setErrorMessage('Bus not found, ');
    
      axios
        .get(`http://localhost:8090/searchbus/${source}/${destination}`)
        .then((response) => {
          if (response.status === 404) {
            setError('');
          }
          return response.data;
        })
        .then((data) => {
          if (data.length === 0) {
            setError('');
          } else {
            setSearchResults(data);
          }
        })
        .catch((error) => {
          setError(error.message);
          console.error(error);
        });
      setSearchClicked(true);
    };
    
    const handleBooking = (bus) => {
        setSelectedBus(bus);
        setRedirect(true);
        setShowResultsTable(false); // Hide the search results table
      };
    
    const handleTransaction = (transactionModel) => {
      return axios
        .post('http://localhost:8090/maketransaction', transactionModel)
        .then((response) => {
          // Handle the response from the transaction endpoint
          console.log('Transaction successful:', response.data);
          return response.data;
        })
        .catch((error) => {
          // Handle errors if the transaction fails
          console.error('Transaction failed:', error);
          throw error;
        });
    };
    

    return (
    <div>
      <h1>Bus Search</h1>
      <input
      type="text"
      placeholder="Source"
      value={source}
      onChange={(e) => setSource(e.target.value)}
      />
      <input
      type="text"
      placeholder="Destination"
      value={destination}
      onChange={(e) => setDestination(e.target.value)}
      />
      <button onClick={handleSearch}>Search</button>
      {searchClick && (
        <div>
          {searchResults.length===0? (
            errorMessage && (
              <p className="error"> {errorMessage} from {source} to {destination} </p>
            )
      ): (
      showResultsTable && ( 
      <><h2> Search results</h2><table className='table table-striped table-bordered'>
                    <thead>
                      <tr>
                        <th>Number Plate</th>
                        <th>Bus Name</th>
                        <th>Capacity</th>
                        <th>Seat Price</th>
                        <th>Starting Time</th>
                        <th>Arrival Time</th>
                        <th>Duration</th>
                        <th>Booking</th>
                      </tr>
                    </thead>
                    <tbody>
                      {searchResults.map((bus) => (
                        <tr key={bus.numberPlate}>
                          <td>{bus.numberPlate}</td>
                          <td>{bus.busName}</td>
                          <td>{bus.capacity}</td>
                          <td>{bus.seatPrice}</td>
                          <td>{bus.startingTime}</td>
                          <td>{bus.arrivalTime}</td>
                          <td>{bus.duration}</td>
                          <td>
                            <button
                              className="btn btn-primary"
                              onClick={() => handleBooking(bus)}
                            >Book
                            </button>
                          </td>
                        </tr>
                      ))}
                    </tbody>
                  </table></>
          ))}
        </div>
      )}
    {redirect && selectedBus && (
      <TransactionForm bus={selectedBus} handleTransaction={handleTransaction} />
    )}
    </div>

    );



};

