            <th>Bus Name</th>
            <th>Capacity</th>
            <th>Seat Price</th>
            <th>Starting Time</th>
            <th>Arrival Time</th>
            <th>Duration</th>
            <th>Booking</th>
            </tr>
            </thead>
            <tbody>
              {searchResults.map((bus) => (
              <tr key={bus.numberPlate}>
                <td>{bus.numberPlate}</td>
                <td>{bus.busName}</td>
                <td>{bus.capacity}</td>
                <td>{bus.seatPrice}</td>
                <td>{bus.startingTime}</td>
                <td>{bus.arrivalTime}</td>
                <td>{bus.duration}</td>
                <td><input className='btn btn-primary' type="submit" value="Book"></input></td>
                </tr>
                ))}
              </tbody>
            </table>
          )}
        </div>
      )}
      </div>
    );



};

export default SearchBus;
