import React, { Component } from 'react';
import { Link } from 'react-router-dom';
import BusService from '../services/BusService';
import { useNavigate } from 'react-router-dom';
class AddBusComponent extends Component {
    navigate=useNavigate();

    // handleClick(){
    //     this.navigate('/getAllBuses');
    // }
    constructor(props){
        super(props)
        this.state={
            numberPlate:'',
            busName:'',
            seatPrice:''
        }
        this.changeNumberPlateHandler=this.changeNumberPlateHandler.bind(this);
        this.changeBusNameHandler=this.changeBusNameHandler.bind(this);
    //  this.saveBus=this.saveBus.bind(this);
    }

    saveBus=(e)=>{
        // let navigate = useNavigate()
        e.preventDefault();
        let bus={numberPlate: this.state.numberPlate, busName: this.state.busName, seatPrice: this.state.seatPrice};
        console.log('bus=>'+JSON.stringify(bus));

        BusService.addBus(bus).then(res =>{
            this.navigate("/getAllBuses");
        });
    }

    changeNumberPlateHandler=(event)=>{
        this.setState({numberPlate:event.target.value});
    }
    changeBusNameHandler=(event)=>{
        this.setState({busName:event.target.value});
    }
    changeSeatPriceHandler=(event)=>{
        this.setState({seatPrice:event.target.value});
    }

    // cancel(){
    //     this.props.history.push('/getAllBuses')
    // }
    render() {
        return (
            <div>
                <div className="contanier">
                    <div className="card col-md-6 offset-md-3 offset-md-3">
                        <h3 className='text-center'>Add Bus</h3>
                        <div className='card-body'>
                            <form>
                                <div className='form-group'>
                                    <label>Number Plate:</label>
                                    <input placeholder='Number Plate' name="numberPlate" className='form-control'
                                    value={this.state.numberPlate} onChange={this.changeNumberPlateHandler}/>
                                </div>
                                <div className='form-group'>
                                    <label>Bus Name:</label>
                                    <input placeholder='Bus Name' name="busName" className='form-control'
                                    value={this.state.busName} onChange={this.changeBusNameHandler}/>
                                </div>
                                <div className='form-group'>
                                    <label>Seat Price:</label>
                                    <input placeholder='Seat Price' name="seatPrice" className='form-control'
                                    value={this.state.seatPrice} onChange={this.changeSeatPriceHandler}/>
                                </div>
                                <button className='btn btn-success' onClick={this.saveBus.bind(this)}>Save</button>
                                {/* <button className='btn btn-danger' to='/getAllBuses' style={{marginLeft: "10px"}}>Cancel</Link> */}
                            </form>
                        </div>
                    </div>
                </div>
            </div>
        );
    }
}

export default AddBusComponent;
