import React, { useState } from 'react';
import axios from 'axios';
import TransactionForm from './TransactionForm';

const BusSearchResults = ({ searchResults, handleBooking }) => {
    return (
      <div className="container non-scrollable">
        <h2>Search results</h2>
        <table className="table table-striped table-bordered">
          <thead>
            <tr>
              <th>Number Plate</th>
              <th>Bus Name</th>
              <th>Capacity</th>
              <th>Seat Price</th>
              <th>Starting Time</th>
              <th>Arrival Time</th>
              <th>Duration</th>
              <th>Booking</th>
            </tr>
          </thead>
          <tbody>
            {searchResults.map((bus) => (
              <tr key={bus.numberPlate}>
                <td>{bus.numberPlate}</td>
                <td>{bus.busName}</td>
                <td>{bus.capacity}</td>
                <td>{bus.seatPrice}</td>
                <td>{bus.startingTime}</td>
                <td>{bus.arrivalTime}</td>
                <td>{bus.duration}</td>
                <td>
                  <button
                    className="btn btn-primary"
                    onClick={() => handleBooking(bus)}
                  >
                    Book
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    );
  };
  

const BusSearchWithCapacity = () => {
  const [capacity, setCapacity] = useState('');
  const [source, setSource] = useState('');
  const [destination, setDestination] = useState('');
  const [searchResults, setSearchResults] = useState([]);
  const [error, setError] = useState('');
  const [selectedBus, setSelectedBus] = useState(null);
  const [searchClick, setSearchClicked] = useState(false);

  const handleSearch = () => {
    setError('');
    setSearchResults([]);

    axios
      .get(`http://localhost:8090/source/destination/getAllBusCapacityMoreThanRequired`, {
        params: {
          capacity: capacity,
          source: source,
          destination: destination
        }
      })
      .then((response) => {
        setSearchResults(response.data);
      })
      .catch((error) => {
        console.log(error.response.data.message);
        setError(error.response.data.message);
      });

      setSearchClicked(true);
  };

  const handleBooking = (bus) => {
    setSelectedBus(bus);
  };

  return (
    <div className="container">
      <h1>Search Bus with Capacity</h1>
      <div className="form-group mb-3">
        <label>Capacity:</label>
        <input
          type="number"
          className="form-control"
          value={capacity}
          onChange={(e) => setCapacity(e.target.value)}
        />
      </div>
      <div className="form-group mb-3">
        <label>Source:</label>
        <input
          type="text"
          className="form-control"
          value={source}
          onChange={(e) => setSource(e.target.value)}
        />
      </div>
      <div className="form-group mb-3">
        <label>Destination:</label>
        <input
          type="text"
          className="form-control"
          value={destination}
          onChange={(e) => setDestination(e.target.value)}
        />
      </div>
      <button className="btn btn-primary mb-3" onClick={handleSearch}>
        Search
      </button>
      {error && <p className="error">{error}</p>}
      {searchResults.length > 0 && (
        <div>
          <h2>Search results</h2>
          <table className="table table-striped table-bordered">
            <thead>
              <tr>
                <th>Number Plate</th>
                <th>Bus Name</th>
                <th>Capacity</th>
                <th>Seat Price</th>
                <th>Starting Time</th>
                <th>Arrival Time</th>
                <th>Duration</th>
                <th>Booking</th>
              </tr>
            </thead>
            <tbody>
              {searchResults.map((bus) => (
                <tr key={bus.numberPlate}>
                  <td>{bus.numberPlate}</td>
                  <td>{bus.busName}</td>
                  <td>{bus.capacity}</td>
                  <td>{bus.seatPrice}</td>
                  <td>{bus.startingTime}</td>
                  <td>{bus.arrivalTime}</td>
                  <td>{bus.duration}</td>
                  <td>
                    <button
                      className="btn btn-primary"
                      onClick={() => handleBooking(bus)}
                    >
                      Book
                    </button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      )}
      {selectedBus && (
        <TransactionForm selectedBus={selectedBus} />
      )}
    </div>
  );
};

export default BusSearchWithCapacity;
