import React, { useState } from 'react';
import axios from 'axios';
import { useHistory } from 'react-router-dom';
const BusFormComponent = () => {
  const history = useHistory();
  const [busData, setBusData] = useState({
    numberPlate: '',
    busName: '',
    source: '',
    destination: '',
    capacity: 0,
    seatPrice: 0,
    startingTime: '',
    arrivalTime: '',
  });

  const handleInputChange = (event) => {
    setBusData({
      ...busData,
      [event.target.name]: event.target.value,
    });
  };

  const saveBusData = async () => {
    try {
      const response = await axios.post('http://localhost:8090/addbus', busData);

      console.log('Bus data saved:', response.data);
      alert('Bus Added !!!!');
      // Handle success or display a success message
    } catch (error) {
      console.error('Error saving bus data:', error);
      // Handle error or display an error message
    }
  };

  const goBack = () => {
    window.location='/'; // Go back to the previous page
  };
  const handleBack = () => {
    history.goBack(); // Navigate back to the previous page
  };
  return (
    <div className="container">
      <div className="card mx-auto mt-5" style={{ maxWidth: '500px' }}>
        <h2 className="card-header text-center">Add Bus</h2>
        <div className="card-body">
          <form>
            <div className="form-group">
              <label>Number Plate:</label>
              <input
                type="text"
                name="numberPlate"
                className="form-control"
                value={busData.numberPlate}
                onChange={handleInputChange}
                autoComplete="off"
              />
            </div>
            <div className="form-group">
              <label>Bus Name:</label>
              <input
                type="text"
                name="busName"
                className="form-control"
                value={busData.busName}
                onChange={handleInputChange}
              />
            </div>
            <div className="form-group">
              <label>Source:</label>
              <input
                type="text"
                name="source"
                className="form-control"
                value={busData.source}
                onChange={handleInputChange}
              />
            </div>
            <div className="form-group">
              <label>Destination:</label>
              <input
                type="text"
                name="destination"
                className="form-control"
                value={busData.destination}
                onChange={handleInputChange}
              />
            </div>
            <div className="form-group">
              <label>Capacity:</label>
              <input
                type="number"
                name="capacity"
                value={busData.capacity}
                className="form-control"
                onChange={handleInputChange}
              />
            </div>
            <div className="form-group">
              <label>Seat Price:</label>
              <input
                type="number"
                name="seatPrice"
                className="form-control"
                value={busData.seatPrice}
                onChange={handleInputChange}
              />
            </div>
            <div className="form-group">
              <label>Starting Time:</label>
              <input
                type="text"
                name="startingTime"
                className="form-control"
                value={busData.startingTime}
                onChange={handleInputChange}
              />
            </div>
            <div className="form-group">
              <label>Arrival Time:</label>
              <input
                type="text"
                name="arrivalTime"
                className="form-control"
                value={busData.arrivalTime}
                onChange={handleInputChange}
              />
            </div>
            <div className="d-flex justify-content-between">
              <button className="btn btn-success mt-3" onClick={saveBusData}>
                Save
              </button>
              <button className="btn btn-primary mt-3" onClick={handleBack}>
                Back
              </button>
            </div>
          </form>
        </div>
      </div>
    </div>
  );
};

export default BusFormComponent;
