import { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';

export function AdminComponent() {
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [password, setPassword] = useState('');
  const [username, setUsername] = useState(localStorage.getItem('userName') || '');

  const handleLogin = (e) => {
    e.preventDefault();
    // Perform login logic here with username and password
    if (username === 'admin' && password === 'admin') {
      setIsLoggedIn(true);
      localStorage.setItem('userName', 'admin');
    } else {
      alert('Admin password or username wrong.');
    }
  };

  const handleLogout = () => {
    // Perform logout logic here
    setIsLoggedIn(false);
    localStorage.removeItem('userName');
    setUsername('');
    window.location = '/';
  };

  // Check if the user is logged in when the component mounts
  useEffect(() => {
    if (localStorage.getItem('userName') === 'admin') {
      setIsLoggedIn(true);
    }
  }, []);

  // Render the admin welcome section when isLoggedIn is true
  if (isLoggedIn) {
    return (
      <div className="container">
        <nav className="navbar navbar-expand-lg navbar-dark bg-primary">
          <Link to="/" className="navbar-brand">Bus Management System</Link>
          <button className="navbar-toggler" type="button" data-toggle="collapse" data-target="#navbarNav"
            aria-controls="navbarNav" aria-expanded="false" aria-label="Toggle navigation">
            <span className="navbar-toggler-icon"></span>
          </button>
          <div className="collapse navbar-collapse" id="navbarNav">
            <ul className="navbar-nav ml-auto">
              <li className="nav-item">
                <Link to="/addbus" className="nav-link">Register Bus</Link>
              </li>
              <li className="nav-item">
                <Link to="/getAllBuses" className="nav-link">View All Buses</Link>
              </li>
              <li className="nav-item">
                <Link to="/numberplate" className="nav-link">Search by Number Plate</Link>
              </li>
              <li className="nav-item">
                <button className="btn btn-danger" onClick={handleLogout}>Logout</button>
              </li>
            </ul>
          </div>
        </nav>
        <div className="container mt-5">
          <h2>Welcome, Admin!</h2>
          {/* Content for the admin section */}
        </div>
      </div>
    );
  } else {
    // Render the login form when isLoggedIn is false
    return (
      <div className="container">
        <nav className="navbar navbar-expand-lg navbar-dark bg-primary">
          <Link to="/" className="navbar-brand">Bus Management System</Link>
        </nav>
        <div className="container mt-5">
          <div className="row justify-content-center">
            <div className="col-md-4">
              <div className="card">
                <div className="card-body">
                  <h2 className="card-title mb-4">Admin Login</h2>
                  <form onSubmit={handleLogin}>
                    <div className="form-group">
                      <label htmlFor="username">Username</label>
                      <input
                        type="text"
                        className="form-control"
                        id="username"
                        placeholder="Username"
                        value={username}
                        onChange={(e) => setUsername(e.target.value)}
                        required
                      />
                    </div>
                    <div className="form-group">
                      <label htmlFor="password">Password</label>
                      <input
                        type="password"
                        className="form-control"
                        id="password"
                        placeholder="Password"
                        value={password}
                        onChange={(e) => setPassword(e.target.value)}
                        required
                      />
                    </div>
                    <button type="submit" className="btn btn-primary btn-block">Login</button>
                  </form>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    );
  }
}
