import React, { useState } from 'react';
import axios from 'axios';

const BusFormComponent = () => {
  const [busData, setBusData] = useState({
    numberPlate: '',
    busName: '',
    source: '',
    destination: '',
    capacity: 0,
    seatPrice: 0,
    startingTime: '',
    arrivalTime: '',
  });

  const handleInputChange = (event) => {
    setBusData({
      ...busData,
      [event.target.name]: event.target.value,
    });
  };

  const saveBusData = async () => {
    try {
      const response = await axios.post('http://localhost:8090/addbus', busData); // Replace '/api/addbus' with your actual endpoint URL

      console.log('Bus data saved:', response.data);
      // Handle success or display a success message
    } catch (error) {
      console.error('Error saving bus data:', error);
      // Handle error or display an error message
    }
  };

  return (
    <div className="contanier">
    <div className="card col-md-6 offset-md-3 offset-md-3"></div>
      <h2 className='text-center'>Add Bus</h2>
      <div className='card-body'></div>
      <form>
      <div className='form-group'>
        <label>Number Plate:</label>
        <input
          type="text"
          name="numberPlate"
          value={busData.numberPlate}
          onChange={handleInputChange}
        />
    </div>
    <div className='form-group'>
        <label>Bus Name:</label>
        <input
          type="text"
          name="busName"
          value={busData.busName}
          onChange={handleInputChange}
        />
        </div>
    <div className='form-group'>
         <label>Source:</label>
        <input
          type="text"
          name="source"
          value={busData.source}
          onChange={handleInputChange}
        />
        </div>
    <div className='form-group'>
        <label>Destination:</label>
        <input
          type="text"
          name="destination"
          value={busData.destination}
          onChange={handleInputChange}
        />
        </div>
        <div className='form-group'>  
        <label>Capacity:</label>
        <input
          type="number"
          name="capacity"
          value={busData.capacity}
          onChange={handleInputChange}
        />
        </div>
        <div className='form-group'> 
        <label>Seat Price:</label>
        <input
          type="number"
          name="seatPrice"
          value={busData.seatPrice}
          onChange={handleInputChange}
        />
        </div>
        <div className='form-group'> 
        <label>Starting Time:</label>
        <input
          type="text"
          name="startingTime"
          value={busData.startingTime}
          onChange={handleInputChange}
        /></div>
        <div className='form-group'> 
        <label>Arrival Time:</label>
        <input
          type="text"
          name="arrivalTime"
          value={busData.arrivalTime}
          onChange={handleInputChange}
        />
        </div>
        <button className='btn btn-success' onClick={saveBusData}>
          Save
        </button>
      </form>
    </div>
  );
};

export default BusFormComponent;
