import React, { useState } from 'react';
import axios from 'axios';
import TransactionForm from './TransactionForm';

const SearchWithNumberPlate = () => {
  const [numberPlate, setNumberPlate]=useState('');
  const [searchResults, setSearchResults] = useState();
  const [searchClick, setSearchClicked] = useState(false);
  const [error, setError] = useState('');
  const [selectedBus, setSelectedBus] = useState(null);

  const handleSearch = () => {
    setError('');
    setSearchResults('');

    axios.get(`http://localhost:8090/getBusByNumberPlate/${numberPlate}`)
      .then(response => {
        if (response.status === 402) {
          throw new Error(``);
        }
        return response.data;
      })
      .then(data => {
      
          console.log('data',data)
          setSearchResults(data);
          console.log('results',searchResults)
      })
      .catch(error => {
        console.log(error.response.data.message);
        setError(error.response.data.message);
      });
    setSearchClicked(true);
  };

  const handleBooking = (bus) => {
    setSelectedBus(bus);
  };

  return (
    <div className="container">
      <h1>Bus Search with Number Plate</h1>
    <div className="mb-3">
        <input
          type="text"
          className="form-control"
          placeholder="Number Plate"
          value={numberPlate}
          onChange={(e) => setNumberPlate(e.target.value)} />
      </div>
      <button className="btn btn-primary" onClick={handleSearch}>
        Search
      </button>
      {searchClick && (
        <div>
          <h2>Search Results</h2>
          {searchResults === null ? (
            <p>{error}</p>
          ) : (
            <table className="table table-striped table-bordered">
              <thead>
                <tr>
                  <th>Number Plate</th>
                  <th>Bus Name</th>
                  <th>Capacity</th>
                  <th>Seat Price</th>
                  <th>Starting Time</th>
                  <th>Arrival Time</th>
                  <th>Duration</th>
                </tr>
              </thead>
              <tbody>
                  <tr >
                    <td>{searchResults.numberPlate}</td>
                    <td>{searchResults.busName}</td>
                    <td>{searchResults.capacity}</td>
                    <td>{searchResults.seatPrice}</td>
                    <td>{searchResults.startingTime}</td>
                    <td>{searchResults.arrivalTime}</td>
                    <td>{searchResults.duration}</td>
                  </tr>
                ))
              </tbody>
            </table>
          )}
        </div>
      )}
      {selectedBus && (
        <TransactionForm selectedBus={selectedBus} />
      )}
    </div>
  );
};

export default SearchWithNumberPlate;
