import React, { useState } from 'react';
import axios from 'axios';

const SearchBusWithName = () => {
    const [source, setSource] = useState('');
    const [destination, setDestination] = useState('');
    const [busName, setBusName] = useState('');
    const [searchResults, setSearchResults] = useState([]);
    const [searchClick, setSearchClicked] = useState(false);
    const [error, setError] = useState('');

const handleSearch = () => {
    setError('');
    setSearchResults([]);
    
    axios.get(`http://localhost:8090/source/destination/busname?busname=${busName}&source=${source}&destination=${destination}`)
        .then(response=>{
            if(response.status===402){
                throw new Error(" ");
            }
            return response.data;
        })
        .then(data=> {
            if(data.length===0){
                setError(' ');
            }
            else{
                setSearchResults(data);
            }
        })
        .catch(error => {
            setError(error.message);
            console.error(error);
        });
    setSearchClicked(true);
    };

    return (
    <div>
      <h1>Bus Search</h1>
      <input
      type="text"
      placeholder="Source"
      value={source}
      onChange={(e) => setSource(e.target.value)}
      />
      <input
      type="text"
      placeholder="Destination"
      value={destination}
      onChange={(e) => setDestination(e.target.value)}
      />
      <input
      type="text"
      placeholder="Bus Name"
      value={busName}
      onChange={(e) =>setBusName(e.target.value)}
    />
      <button onClick={handleSearch}>Search</button>
      {searchClick && (
        <div>
          <h2>Search Results</h2>
          {searchResults.length===0? (
        <p>Sorry, No Bus found from {source} to {destination} !!</p>
      ): (
      <table className='table table-striped table-bordered'>
        <thead>
          <tr>
            <th>Number Plate</th>
            <th>Bus Name</th>
            <th>Capacity</th>
            <th>Seat Price</th>
            <th>Starting Time</th>
            <th>Arrival Time</th>
            <th>Duration</th>
            </tr>
            </thead>
            <tbody>
              {searchResults.map((bus) => (
              <tr key={bus.numberPlate}>
                <td>{bus.numberPlate}</td>
                <td>{bus.busName}</td>
                <td>{bus.capacity}</td>
                <td>{bus.seatPrice}</td>
                <td>{bus.startingTime}</td>
                <td>{bus.arrivalTime}</td>
                <td>{bus.duration}</td>
                </tr>
                ))}
              </tbody>
            </table>
          )}
        </div>
      )}
      </div>
    );



};

export default SearchBusWithName;
