import { Link } from 'react-router-dom';
import busImage from './bus.jpg';

export function Home(props) {
  const containerStyle = {
    backgroundImage: `url(${busImage})`,
    backgroundSize: 'cover',
    backgroundPosition: 'center',
    backgroundRepeat: 'no-repeat',
  };

  return (
    <div className="container-fluid d-flex flex-column justify-content-center align-items-center home-container" style={containerStyle}>
      <h2 className="title mt-5">Bus Booking Website</h2>
      <p className="description">Easy website for booking buses and searching buses!!</p>
      <p className="description">Also, add and delete bus details dynamically in a few clicks.</p>
      <div className="buttons">
        <Link to="/admin" className="btn btn-primary adminButton">Login as Admin</Link>
        <Link to="/user" className="btn btn-primary userButton">Login as User</Link>
      </div>
    </div>
  );
}
