import React from 'react';
import { Home } from './Home';

const Payment = ({ transaction, data }) => {
  // Handle payment process and display payment details
  const handlePayment = () => {
    // Implement your payment logic here
    console.log('Payment process initiated');
    // Display payment success message
    alert('Payment successful! Enjoy the ride!');
  };
  const handleCancel = () => {
    // Redirect to UserComponent
    window.location = '/user';
  };
  return (
    <div className="container">
      <div className="card mt-4 ">
        <div className="card-body text-center">
          <h2 className="card-title">Payment Details</h2>
          <div className="card-text">
            <p>
              <strong>Transaction ID:</strong> {transaction.transactionId}
            </p>
            <p>
              <strong>No. of Passengers:</strong> {transaction.noOfPassenger}
            </p>
            <p>
              <strong>Bus Name & Number Plate:</strong> {transaction.busName} {transaction.numberPlate}
            </p>
            <p>
              <strong>Total Price:</strong> {transaction.totalPrice}
            </p>
            <p className="text-success">
              <strong>You got {transaction.discount} Discount!</strong>
            </p>
          </div>
          <button className="btn btn-primary" onClick={handlePayment}>
            Pay
          </button>
          <button to="/user" className="btn btn-secondary mt-auto" onClick={handleCancel}>
            Cancel
          </button>
        </div>
      </div>
      {data && (
        <div className="card mt-4">
          <div className="card-body">
            <h3 className="card-title">Additional Data</h3>
            <pre>{JSON.stringify(data, null, 2)}</pre>
          </div>
        </div>
      )}
    </div>
  );
};

export default Payment;
