import React, {useState, useEffect} from 'react'
import { Link } from 'react-router-dom'
import BusService from '../services/BusService'

const GetAllBusComponent = () => {

    const [buses, setBuses] = useState([])

    useEffect(() => {

        getAllBuses();
    }, [])

    const getAllBuses = () => {
        BusService.getAllBuses().then((response) => {
            setBuses(response.data)
            console.log(response.data);
        }).catch(error =>{
            console.log(error);
        })
    }

    // const deleteEmployee = (employeeId) => {
    //    EmployeeService.deleteEmployee(employeeId).then((response) =>{
    //     getAllEmployees();

    //    }).catch(error =>{
    //        console.log(error);
    //    })
        
    // }

    return (
        <div className = "container">
            <h2 className = "text-center"> List Employees </h2>
            <Link to = "/addbus" className = "btn btn-primary mb-2" > Add Bus </Link>
            <table className='table table-striped table-bordered'>
                        <thead>
                            <tr>
                                <th> Number Plate</th>
                                <th> Bus Name </th>
                                <th> Source </th>
                                <th> Destination </th>
                                <th> Capacity </th>
                                <th> Seat Price </th>
                                <th> Starting Time </th>
                                <th> Arrival Time </th>
                                <th> Duration </th>
                                <th> Actions </th>
                            </tr>
                        </thead>
                        <tbody>
                    {
                        buses.map(
                            bus =>
                            <tr key= {bus.numberPlate}>
                                        <td> {bus.numberPlate} </td>
                                        <td> {bus.busName} </td>
                                        <td> {bus.source} </td>
                                        <td> {bus.destination} </td>
                                        <td> {bus.capacity} </td>
                                        <td> {bus.seatPrice} </td>
                                        <td> {bus.startingTime} </td>
                                        <td> {bus.arrivalTime} </td>
                                        <td> {bus.duration} </td>
                            </tr>
                        )
                    }
                </tbody>
            </table>
        </div>
    )
}

export default GetAllBusComponent;
