import { useState } from 'react';
import { Link } from 'react-router-dom';

export function AdminComponent() {
  const [isLoggedIn, setIsLoggedIn] = useState(false);

  const handleLogin = () => {
    // Perform login logic here
    setIsLoggedIn(true);
  };

  const handleLogout = () => {
    // Perform logout logic here
    setIsLoggedIn(false);
    window.location = '/';

  };

  return (
    <div className="user-login text-center mt-5">
      {!isLoggedIn ? (
        <div className="login-form ">
          <h2 className="mb-3">Continue as same admin</h2>
          <button className="btn btn-primary" onClick={handleLogin}>Continue</button>
        </div>
      ) : (
        <div>
          <div className="welcome-message mb-3">
            <h2>Welcome, Admin!</h2>
            <button className="btn btn-danger mt-4 mb-5" onClick={handleLogout}>Logout</button>
          </div>
          <div className="search-buttons">
            <Link to="/addbus" className="btn btn-primary mt-5 mb-2" >Register Bus</Link>
            <br></br>
            <Link to="/getAllBuses" className="btn btn-primary mt-3 mb-2" >View All Bus</Link>
            <br></br>
            <Link to="/numberplate" className="btn btn-primary mt-3 mb-2" >Search with Number Plate</Link>
          </div>
        </div>
      )}
    </div>
  );
}
