import React, { useState } from 'react';
import axios from 'axios';

const BusFormComponent = () => {
  const [busData, setBusData] = useState({
    numberPlate: '',
    busName: '',
    source: '',
    destination: '',
    capacity: 0,
    seatPrice: 0,
    startingTime: '',
    arrivalTime: '',
  });

  const handleInputChange = (event) => {
    setBusData({
      ...busData,
      [event.target.name]: event.target.value,
    });
  };

  const saveBusData = async () => {
    try {
      const response = await axios.post('http://localhost:8090/addbus', busData); // Replace '/api/addbus' with your actual endpoint URL

      console.log('Bus data saved:', response.data);
      // Handle success or display a success message
    } catch (error) {
      console.error('Error saving bus data:', error);
      // Handle error or display an error message
    }
  };

  return (
    <div>
      <h2>Add Bus</h2>
      <form>
        <label>Number Plate:</label>
        <input
          type="text"
          name="numberPlate"
          value={busData.numberPlate}
          onChange={handleInputChange}
        />

        <label>Bus Name:</label>
        <input
          type="text"
          name="busName"
          value={busData.busName}
          onChange={handleInputChange}
        />
         <label>Source:</label>
        <input
          type="text"
          name="source"
          value={busData.source}
          onChange={handleInputChange}
        />

        <label>Destination:</label>
        <input
          type="text"
          name="destination"
          value={busData.destination}
          onChange={handleInputChange}
        />

        <label>Capacity:</label>
        <input
          type="number"
          name="capacity"
          value={busData.capacity}
          onChange={handleInputChange}
        />

        <label>Seat Price:</label>
        <input
          type="number"
          name="seatPrice"
          value={busData.seatPrice}
          onChange={handleInputChange}
        />

        <label>Starting Time:</label>
        <input
          type="text"
          name="startingTime"
          value={busData.startingTime}
          onChange={handleInputChange}
        />
        <label>Arrival Time:</label>
        <input
          type="text"
          name="arrivalTime"
          value={busData.arrivalTime}
          onChange={handleInputChange}
        />

        <button type="button" onClick={saveBusData}>
          Save
        </button>
      </form>
    </div>
  );
};

export default BusFormComponent;
