import { useState } from 'react';
import { Link } from 'react-router-dom';

export function UserComponent() {
  const [isLoggedIn, setIsLoggedIn] = useState(false);

  const handleLogin = () => {
    // Perform login logic here
    setIsLoggedIn(true);
  };

  const handleLogout = () => {
    // Perform logout logic here
    setIsLoggedIn(false);
  };

  return (
    <div className="user-login text-center mt-5">
      {!isLoggedIn ? (
        <div className="login-form ">
          <h2 className="mb-3">Login as User</h2>
          <button className="btn btn-primary" onClick={handleLogin}>Login</button>
        </div>
      ) : (
        <div>
          <div className="welcome-message mb-3">
            <h2>Welcome, User!</h2>
            <button className="btn btn-danger mt-4 mb-4" onClick={handleLogout}>Logout</button>
          </div>
          <div className="search-buttons">
            <Link to="/searchBus" className="btn btn-primary" >Search Bus</Link>
            <br></br>
            <Link to="/searchBusWithName" className="btn btn-primary" >Search Bus with Name</Link>
            <br></br>
            <Link to="/searchWithNumberPlate" className="btn btn-primary" >Search with Number Plate</Link>
          </div>
        </div>
      )}
    </div>
  );
}
