import React, { useState } from 'react';
import axios from 'axios';
import TransactionForm from './TransactionForm';

const SearchBusWithName = () => {
  const [source, setSource] = useState('');
  const [destination, setDestination] = useState('');
  const [busName, setBusName] = useState('');
  const [searchResults, setSearchResults] = useState([]);
  const [searchClick, setSearchClicked] = useState(false);
  const [error, setError] = useState('');
  const [selectedBus, setSelectedBus] = useState(null);

  const handleSearch = () => {
    setError('');
    setSearchResults([]);

    axios.get(`http://localhost:8090/source/destination/busname?busName=${busName}&source=${source}&destination=${destination}`)
      .then(response => {
        if (response.status === 402) {
          throw new Error(`No buses found with bus name equal to ${busName} from ${source} to ${destination}`);
        }
        return response.data;
      })
      .then(data => {
        if (data.length === 0) {
          setError('Error, Bus not found.');
        } else {
          setSearchResults(data);
        }
      })
      .catch(error => {
        console.log(error.response.data.message);
        setError(error.response.data.message);
      });
    setSearchClicked(true);
  };

  const handleBooking = (bus) => {
    setSelectedBus(bus);
  };

  return (
    <div className="container non-scrollable">
      <h1>Bus Search</h1>
      <div className="mb-3">
        <input
          type="text"
          className="form-control"
          placeholder="Source"
          value={source}
          onChange={(e) => setSource(e.target.value)}
        />
      </div>
      <div className="mb-3">
        <input
          type="text"
          className="form-control"
          placeholder="Destination"
          value={destination}
          onChange={(e) => setDestination(e.target.value)}
        />
      </div>
      <div className="mb-3">
        <input
          type="text"
          className="form-control"
          placeholder="Bus Name"
          value={busName}
          onChange={(e) => setBusName(e.target.value)}
        />
      </div>
      <button className="btn btn-primary" onClick={handleSearch}>
        Search
      </button>
      {searchClick && (
        <div>
          <h2>Search Results</h2>
          {searchResults.length === 0 ? (
            <p>{error}</p>
          ) : (
            <table className="table table-striped table-bordered">
              <thead>
                <tr>
                  <th>Number Plate</th>
                  <th>Bus Name</th>
                  <th>Capacity</th>
                  <th>Seat Price</th>
                  <th>Starting Time</th>
                  <th>Arrival Time</th>
                  <th>Duration</th>
                  <th>Booking</th>
                </tr>
              </thead>
              <tbody>
                {searchResults.map((bus) => (
                  <tr key={bus.numberPlate}>
                    <td>{bus.numberPlate}</td>
                    <td>{bus.busName}</td>
                    <td>{bus.capacity}</td>
                    <td>{bus.seatPrice}</td>
                    <td>{bus.startingTime}</td>
                    <td>{bus.arrivalTime}</td>
                    <td>{bus.duration}</td>
                    <td>
                      <button className="btn btn-primary" onClick={() => handleBooking(bus)}>
                        Book
                      </button>
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          )}
        </div>
      )}
      {selectedBus && (
        <TransactionForm selectedBus={selectedBus} />
      )}
    </div>
  );
};

export default SearchBusWithName;
