import React, { Component } from 'react';
import { Link } from 'react-router-dom';
import BusService from '../services/BusService';
import { useNavigate } from 'react-router-dom';

class BusListComponent extends Component {
    navigate=useNavigate();

    handleClick(){
        this.navigate('/getAllBuses');
    }
constructor(props){
    super(props)

    this.state={
        buses: []
    }
    // this.addBus=this.addBus.bind(this);
}
    componentDidMount(){
        //save response data bus array
        BusService.getBuses().then((res)=>{
            this.setState({buses: res.data})
        });
    }

    // addBus(){
    //     let navigate = useNavigate()
    //     this.props.navigate('/addbus');
    // }

    render() {
        return (
            <div>
                <h2 className='text-center'>Bus List</h2>
                <button className='btn btn-primary' onClick={this.handleClick.bind(this)}> Add Bus</button>
                <div className='row'>
                    <table className='table table-striped table-bordered'>
                        <thead>
                            <tr>
                                <th> Number Plate</th>
                                <th> Bus Name </th>
                                <th> Source </th>
                                <th> Destination </th>
                                <th> Capacity </th>
                                <th> Seat Price </th>
                                <th> Starting Time </th>
                                <th> Arrival Time </th>
                                <th> Duration </th>
                                <th> Actions </th>
                            </tr>
                        </thead>

                        <tbody>
                            {
                                this.state.buses.map(
                                    bus=>
                                    <tr key= {bus.numberPlate}>
                                        <td> {bus.numberPlate} </td>
                                        <td> {bus.busName} </td>
                                        <td> {bus.source} </td>
                                        <td> {bus.destination} </td>
                                        <td> {bus.capacity} </td>
                                        <td> {bus.seatPrice} </td>
                                        <td> {bus.startingTime} </td>
                                        <td> {bus.arrivalTime} </td>
                                        <td> {bus.duration} </td>
                                    </tr>
                                )
                            }   
                        </tbody>
                    </table>
                </div>
            </div>
        );
    }
}

export default BusListComponent;
