import React, { Component } from 'react';
import BusService from '../services/BusService';

class BusListComponent extends Component {
constructor(props){
    super(props)

    this.state={
        buses: []
    }
}
    componentDidMount(){
        //save response data bus array
        BusService.getBuses().then((res)=>{
            this.setState({buses: res.data})
        });
    }
    render() {
        return (
            <div>
                <h2 className='text-center'>Bus List</h2>
                <div className='row'>
                    <table className='table table-striped table-bordered'>
                        <thead>
                            <tr>
                                <th> Number Plate</th>
                                <th> Bus Name </th>
                                <th> Source </th>
                                <th> Destination </th>
                                <th> Capacity </th>
                                <th> Seat Price </th>
                                <th> Starting Time </th>
                                <th> Arrival Time </th>
                                <th> Duration </th>
                                <th> Actions b </th>
                            </tr>
                        </thead>

                        <tbody>
                            {
                                this.state.buses.map(
                                    bus=>
                                    <tr key= {bus.numberPlate}>
                                        <td> {bus.numberPlate} </td>
                                        <td> {bus.busName} </td>
                                        <td> {bus.source} </td>
                                        <td> {bus.destination} </td>
                                        <td> {bus.capacity} </td>
                                        <td> {bus.seatPrice} </td>
                                        <td> {bus.startingTime} </td>
                                        <td> {bus.arrivalTime} </td>
                                        <td> {bus.duration} </td>
                                    </tr>
                                )
                            }   
                        </tbody>
                    </table>
                </div>
            </div>
        );
    }
}

export default BusListComponent;
