import React, { useState } from 'react';
import DatePicker from 'react-datepicker';
import 'react-datepicker/dist/react-datepicker.css';

const TransactionForm = ({
  selectedBus,
  noOfPassenger,
  tripDate,
  email,
  handleNoOfPassengerChange,
  handlePassengerChange,
  handleSubmit,
}) => {
  const [passengerList, setPassengerList] = useState([]);

  const handleAddPassenger = () => {
    setPassengerList((prevList) => [...prevList, {}]);
  };

  const handleRemovePassenger = (index) => {
    setPassengerList((prevList) => prevList.filter((_, i) => i !== index));
  };

  const handleSetTripDate = (date) => {
    setTripDate(date);
  };

  const handleSetEmail = (email) => {
    setEmail(email);
  };

  return (
    <div style={{ overflowY: 'auto', maxHeight: '500px' }}>
      <h2>Transaction Form</h2>
      <div className="form-group" style={{ overflowY: 'auto', maxHeight: '500px' }}>
        <label>Number Plate:</label>
        <input
          type="text"
          className="form-control"
          value={selectedBus.numberPlate}
          disabled
        />
      </div>
      <div className="form-group">
        <label>Source:</label>
        <input
          type="text"
          className="form-control"
          value={selectedBus.source}
          disabled
        />
      </div>
      <div className="form-group">
        <label>Destination:</label>
        <input
          type="text"
          className="form-control"
          value={selectedBus.destination}
          disabled
        />
      </div>
      <div className="form-group">
        <label>Starting Time:</label>
        <input
          type="text"
          className="form-control"
          value={selectedBus.startingTime}
          disabled
        />
      </div>
      <div className="form-group">
        <label>No. of Passengers:</label>
        <input
          type="number"
          className="form-control"
          value={noOfPassenger}
          onChange={(e) => handleNoOfPassengerChange(parseInt(e.target.value))}
        />
      </div>
      <div className="form-group">
        <label>Trip Date:</label>
        <DatePicker
          selected={tripDate}
          onChange={handleSetTripDate}
          className="form-control"
        />
      </div>
      <div className="form-group">
        <label>Email:</label>
        <input
          type="text"
          className="form-control"
          value={email}
          onChange={(e) => handleSetEmail(e.target.value)}
        />
      </div>
      <div className="form-group" style={{ overflowY: 'auto', maxHeight: '300px' }}>
        {Array.from({ length: noOfPassenger }).map((_, index) => (
          <div key={index}>
            <h3>Passenger {index + 1}</h3>
            <div className="form-group">
              <label>Name:</label>
              <input
                type="text"
                className="form-control"
                value={passengerList[index]?.passengerName || ''}
                onChange={(e) =>
                  handlePassengerChange(index, 'passengerName', e.target.value)
                }
              />
            </div>
            <div className="form-group">
              <label>Age:</label>
              <input
                type="number"
                className="form-control"
                value={passengerList[index]?.passengerAge || ''}
                onChange={(e) =>
                  handlePassengerChange(index, 'passengerAge', parseInt(e.target.value))
                }
              />
            </div>
            <div className="form-group">
              <label>Gender:</label>
              <select
                className="form-control"
                value={passengerList[index]?.passengerGender || ''}
                onChange={(e) =>
                  handlePassengerChange(index, 'passengerGender', e.target.value)
                }
              >
                <option value="">Select</option>
                <option value="Male">Male</option>
                <option value="Female">Female</option>
                <option value="Other">Other</option>
              </select>
            </div>
            <button
              className="btn btn-danger"
              onClick={() => handleRemovePassenger(index)}
            >
              Remove Passenger
            </button>
          </div>
        ))}
      </div>
      <button className="btn btn-primary" onClick={handleSubmit}>
        Submit
      </button>
      <button className="btn btn-success" onClick={handleAddPassenger}>
        Add Passenger
      </button>
    </div>
  );
};

export default TransactionForm;
